import { useEffect, useState } from "react";
import api from "../services/api";

const AdminDashboard = () => {
  const [docs, setDocs] = useState([]);

  useEffect(() => {
    api.get("/docs/pending").then((res) => setDocs(res.data));
  }, []);

  const approveDoc = async (id) => {
    await api.put(`/docs/approve/${id}`);
    setDocs(docs.filter((d) => d._id !== id));
  };

  return (
    <div className="p-6">
      <h2 className="text-xl font-bold mb-4">Pending Documents</h2>

      {docs.map((d) => (
        <div key={d._id} className="border p-3 mb-2">
          <p>Student ID: {d.studentId}</p>
          <button
            onClick={() => approveDoc(d._id)}
            className="bg-blue-600 text-white px-3 py-1 mt-2"
          >
            Approve
          </button>
        </div>
      ))}
    </div>
  );
};

export default AdminDashboard;
