import { useState } from "react";
import api from "../services/api";

const FacultyDashboard = () => {
  const [file, setFile] = useState(null);
  const [studentId, setStudentId] = useState("");
  const [semester, setSemester] = useState("");
  const [loading, setLoading] = useState(false);

  const uploadHandler = async () => {
    if (!file || !studentId || !semester) {
      alert("Please fill all fields");
      return;
    }

    try {
      setLoading(true);

      const formData = new FormData();
      formData.append("file", file);
      formData.append("studentId", studentId);
      formData.append("semester", semester);

      await api.post("/docs/upload", formData);

      alert("Document uploaded successfully (demo)");
      setFile(null);
      setStudentId("");
      setSemester("");
    } catch (err) {
      alert("Upload failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen p-6 bg-gray-100">
      <div className="max-w-xl p-8 mx-auto bg-white rounded shadow">
        <h2 className="mb-2 text-2xl font-bold">
          Upload Marksheet
        </h2>

        <p className="mb-6 text-gray-500">
          Upload academic documents for students. Documents will
          require admin approval before students can view them.
        </p>

        {/* Student ID */}
        <input
          type="text"
          placeholder="Student ID or Email"
          className="w-full p-3 mb-4 border rounded"
          value={studentId}
          onChange={(e) => setStudentId(e.target.value)}
        />

        {/* Semester */}
        <input
          type="text"
          placeholder="Semester (e.g. 1, 2, 3)"
          className="w-full p-3 mb-4 border rounded"
          value={semester}
          onChange={(e) => setSemester(e.target.value)}
        />

        {/* File */}
        <input
          type="file"
          className="w-full p-3 mb-6 border rounded"
          onChange={(e) => setFile(e.target.files[0])}
        />

        <button
          onClick={uploadHandler}
          disabled={loading}
          className="w-full py-3 text-white transition bg-green-600 rounded hover:bg-green-700 disabled:opacity-50"
        >
          {loading ? "Uploading..." : "Upload Document"}
        </button>
      </div>
    </div>
  );
};

export default FacultyDashboard;
