import { useEffect, useState } from "react";
import api from "../services/api";

const AdminDashboard = () => {
  const [docs, setDocs] = useState([]);
  const [loading, setLoading] = useState(false);

useEffect(() => {
  const fetchPendingDocs = async () => {
    try {
      const res = await api.get("/docs/pending");
      setDocs(res.data);
    } catch (err) {
      console.error("Failed to fetch pending documents", err);
    }
  };

  fetchPendingDocs();
}, []);


  const approveDoc = async (id) => {
    try {
      setLoading(true);

      // await api.put(`/docs/approve/${id}`);
      alert("Document approved (demo)");

      setDocs(docs.filter((d) => d._id !== id));
    } catch (err) {
      alert("Approval failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen p-6 bg-gray-100">
      <div className="max-w-5xl mx-auto">
        <h2 className="mb-6 text-2xl font-bold">
          Pending Documents
        </h2>

        {/* Empty State */}
        {docs.length === 0 && (
          <div className="p-6 text-center bg-white rounded shadow">
            <p className="text-gray-600">
              No pending documents.
            </p>
            <p className="mt-2 text-sm text-gray-400">
              All uploaded documents have been reviewed.
            </p>
          </div>
        )}

        {/* Pending Documents */}
        {docs.map((doc) => (
          <div
            key={doc._id}
            className="flex items-center justify-between p-5 mb-4 bg-white rounded shadow"
          >
            <div>
              <p className="font-semibold">
                Student ID: {doc.studentId}
              </p>
              <p className="text-sm text-gray-600">
                Semester: {doc.semester}
              </p>
              <p className="text-sm text-yellow-600">
                Status: Pending
              </p>
            </div>

            <button
              onClick={() => approveDoc(doc._id)}
              disabled={loading}
              className="px-4 py-2 text-white transition bg-blue-600 rounded hover:bg-blue-700 disabled:opacity-50"
            >
              Approve
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
